$(document).ready(function () {
  const modal = document.querySelector(".modal");
  const showModal = document.querySelectorAll(".btn-modal");
  const hiddenModal = document.querySelector(".modal-hidden");
  const hiddenModalFon = document.querySelector(".modal-inner");
  const menuBlur = document.querySelector("#menu__toggle");
  const Main = document.querySelector(".main");
  const Intro = document.querySelector(".intro");
  const itemMenu = document.querySelectorAll(".menu__item");
  const boxMenu = document.querySelector(".menu__box");
  const dropdown = document.querySelector(".dropdown");
  const dropdownContent = document.querySelector(".question-content");
  // dropdown.addEventListener("click", function (e) {
  //   if (e.target.classList == "dropdown") {
  //     e.dropdownContent;
  //     console.log(e.dropdownContent);
  //   }
  // });

  // showModal.addEventListener("click", function (e) {
  //   modal.classList.toggle("modal-active");
  //   pageInner.classList.toggle("blur");
  //   Main.classList.toggle("blur");
  //   Footer.classList.toggle("blur");
  // });
  hiddenModal.addEventListener("click", function (e) {
    modal.classList.remove("modal-active");
  });

  menuBlur.addEventListener("click", function (e) {
    Main.classList.toggle("blur");
    Intro.classList.toggle("blur");
  });
  // showModal.forEach(function (userItem) {
  //   modal.classList.toggle("modal-active");
  // });
  $(".btn-modal").click(function () {
    $(".modal").toggleClass("modal-active");
  });

  $(".modal__item").click(function () {
    $(".modal__item").toggleClass("modal__item--active");
  });
  // showModal.forEach(function (userItem) {
  //   modal.classList.toggle("modal-active");
  // });

  hiddenModalFon.addEventListener("click", function (e) {
    if (e.target.classList == "modal-inner") {
      modal.classList.remove("modal-active");
    }
  });

  boxMenu.addEventListener("click", function (e) {
    if (e.target.classList == "menu__item") {
      document.querySelector("#menu__toggle").checked = false;
      Main.classList.remove("blur");
      Intro.classList.remove("blur");
    }
  });

  //time||||||||||||||||||||||||||||||||||||||||||||||||||||||||||
  // Set the date we're counting down to
  var countDownDate = new Date("Aug 20, 2020 15:37:25").getTime();

  // Update the count down every 1 second
  var x = setInterval(function () {
    // Get today's date and time
    var now = new Date().getTime();

    // Find the distance between now and the count down date
    var distance = countDownDate - now;

    // Time calculations for days, hours, minutes and seconds
    var days = Math.floor(distance / (1000 * 60 * 60 * 24));
    var hours = Math.floor(
      (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
    );
    var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((distance % (1000 * 60)) / 1000);

    // Display the result in the element with id="demo"
    document.getElementById("day").innerHTML = days;
    document.getElementById("hours").innerHTML = hours;
    document.getElementById("seconds").innerHTML = seconds;

    // If the count down is finished, write some text
    if (distance < 0) {
      clearInterval(x);
      document.getElementById("seconds").innerHTML = "EXPIRED";
    }
  }, 1000);
  // time\\\\\\\\\\\\\\
  if ($(window).width() < 767) {
    $("ul.dropdown > .question-inner").click(function () {
      $(this).parent().toggleClass("open");
      $(this).next().slideToggle();
    });
  }
  $("ul.dropdown > .question-inner").click(function () {
    $(this).parent().toggleClass("open");
    $(this).next().slideToggle();
  });

  $(document).ready(function () {
    $("#footer-menu").on("click", "a", function (event) {
      event.preventDefault();
      var id = $(this).attr("href"),
        top = $(id).offset().top;
      $("body,html").animate({ scrollTop: top }, 1000);
    });
    $("#menu").on("click", "a", function (event) {
      event.preventDefault();
      var id = $(this).attr("href"),
        top = $(id).offset().top;
      $("body,html").animate({ scrollTop: top }, 1000);
    });
    $("#menu-2").on("click", "a", function (event) {
      event.preventDefault();
      var id = $(this).attr("href"),
        top = $(id).offset().top;
      $("body,html").animate({ scrollTop: top }, 1000);
    });
  });
  $(".btn-email").on("click", function () {
    let email = $("#email_popup").val().trim();
    let teleg = $("#teleg_popup").val().trim();

    if (email == "") {
      $("#errorMessege").text("Введите почту");
      return false;
    } else if (teleg == "") {
      $("#errorMessege").text("Введите данные");
      return false;
    }

    $("#errorMessege").text("");

    $.ajax({
      url: "form_popup.php",
      type: "POST",
      cash: false,
      data: { email: $email_popup, teleg: $teleg_popup },
      processData: false,
      contentType: false,
      dataType: "text",
      beforeSend: function () {
        $(".btn-email").prop("disabled", true);
      },
      success: function (data) {
        if (!data) alert("Были ошибки, сообщение не отправлено!");
        else $(".modal-input").trigger("reset");

        $(".btn-email").prop("disabled", false);
      },
    });
  });
});
