$(document).ready(function () {
  const showModal = document.querySelector(".btn-modal");
  const showModal_2 = document.querySelector(".btn-modal--main");
  const hiddenModal = document.querySelector(".modal-hidden");
  const hiddenModalFon = document.querySelector(".modal-inner");
  const Body = document.querySelector(".body");
  const modal = document.querySelector(".modal");
  const pageInner = document.querySelector(".page__inner");
  const Main = document.querySelector(".main");
  const Footer = document.querySelector(".footer");
  const itemMenu = document.querySelectorAll(".menu__item");
  const boxMenu = document.querySelector(".menu__box");

  showModal.addEventListener("click", function () {
    modal.classList.toggle("modal-active");
    pageInner.classList.toggle("blur");
    Main.classList.toggle("blur");
    Footer.classList.toggle("blur");
  });

  showModal_2.addEventListener("click", function () {
    modal.classList.toggle("modal-active");
    pageInner.classList.toggle("blur");
    Main.classList.toggle("blur");
    Footer.classList.toggle("blur");
  });

  hiddenModalFon.addEventListener("click", function (e) {
    if (e.target.classList == "modal-inner") {
      modal.classList.remove("modal-active");
      pageInner.classList.remove("blur");
      Main.classList.remove("blur");
      Footer.classList.remove("blur");
    }
  });
  hiddenModal.addEventListener("click", function (e) {
    modal.classList.remove("modal-active");
    pageInner.classList.remove("blur");
    Main.classList.remove("blur");
    Footer.classList.remove("blur");
  });

  boxMenu.addEventListener("click", function (e) {
    if (e.target.classList == "menu__item") {
      document.querySelector("#menu__toggle").checked = false;
      console.log("kks");
    }
  });

  var swiper = new Swiper(".be-wrapp", {
    slidesPerView: 1,
    navigation: {
      nextEl: ".main-slider-next",
      prevEl: ".main-slider-prev",
    },
  });

  var swiper_3 = new Swiper(".extra-swiper", {
    slidesPerView: 2,
  });

  var galleryThumbs = new Swiper(".gallery-thumbs", {
    spaceBetween: 10,
    slidesPerView: 4,
    loop: true,
    freeMode: true,
    loopedSlides: 5, //looped slides should be the same
    watchSlidesVisibility: true,
    watchSlidesProgress: true,
  });
  var galleryTop = new Swiper(".gallery-top", {
    spaceBetween: 10,
    loop: true,
    loopedSlides: 5, //looped slides should be the same
    navigation: {
      nextEl: ".main-slider-next--1",
      prevEl: ".main-slider-prev--1",
    },
    thumbs: {
      swiper: galleryThumbs,
    },
  });
  var swiper_4 = new Swiper(".reviews-wrapp", {
    slidesPerView: 1,
    navigation: {
      nextEl: ".main-slider-next--2",
      prevEl: ".main-slider-prev--2",
    },
    pagination: {
      el: ".swiper-pagination",
    },
    spaceBetween: 100,
  });
});
